<script lang='ts'>
	import { createEventDispatcher } from "svelte";

    const dispatch = createEventDispatcher()

    let checked = false

    $: handleIsChecked(checked)

    function handleIsChecked(checked : boolean) {
        if (checked) {
            dispatch('agreed')
        }
    }
</script>

